<?php # introducimos en una variable el nombre de la base de datos a crear $crear="ejemplos"; #conectamos con el servidor y comprobamos la conexión if ($conexion=mysqli_connect ("localhost","root","")){         echo "<h2> Conexión establecida con el servidor</h2><br>";             $p=mysqli_query($conexion,"SHOW DATABASES");                          $numero=mysqli_num_rows($p);             $comprueba=0; #comprobamos si existe una base con ese nombre # si existe hace la variable comprueba igual a 1 # si no existe la variable comprueba sera 0 tal como la hemos # puesto aquí arriba while ($fila=mysqli_fetch_array($p)) {              if ($crear==$fila[0]){         $comprueba=1;         }; } #si la base de datos no existe la creamos y escribimos el mensaje de exito #si existe, avisamos de su existencia y evitamos intentar crearla # observa que como nombre del la nueva base puede ponerse una variableif($comprueba==0){     if(mysqli_query ($conexion,"CREATE DATABASE $crear")){             echo "<h2> Base de datos $crear creada</h2><br>";           }else{             echo "<h2> No ha sido posible crear la base de datos $crear</h2><br>";                };      }else{     echo "<h2> La base de datos $crear YA EXISTE</h2><br>";             } ; # cerramos la conexión con el servidor if(mysqli_close($conexion)){             echo "<h2> Conexión cerrada con exito</h2><br>";                          }else{         echo "<h2> No se ha cerrado la conexión</h2>";              };     }else{         echo "<h2> NO HA SIDO POSIBLE ESTABLECER LA CONEXIÓN</h2>"; } ?> 